(()=>{window.Webflow||(window.Webflow=[]);window.Webflow.push(()=>{fetch("https://api.rippling.com/platform/api/ats/v1/board/surepath-ai/jobs").then(t=>t.json()).then(t=>{let c=[...new Set(t.map(o=>o.department.label))],a=document.querySelector(".job_component");c.forEach(o=>{let n=document.querySelector('[fs-element="department-wrap"]').cloneNode(!0);n.querySelectorAll('[fs-element="department"]').forEach(e=>{e.textContent=o});let m=t.filter(e=>e.department.label===o);n.querySelector('[fs-element="job"]').remove(),m.forEach(e=>{let r=document.querySelector('[fs-element="job"]').cloneNode(!0),p=r.querySelector('[fs-element="job-link"]');p.href=e.url;let l=r.querySelector('[fs-element="job-title"]');l.textContent=e.name,l.href=e.url,r.querySelector('[fs-element="location"]').textContent=e.workLocation.label,n.appendChild(r)}),a.appendChild(n)}),document.querySelector('[fs-element="department-wrap"]').remove()}).catch(t=>console.error("Error fetching jobs:",t))});})();
